$("document").ready(function(){$(".navbar-toggle").on("click",function(){$(".collapse, .main-content-container").toggleClass("in")})}),$(window).resize(function(){$(window).width()>768&&$(".collapse, .main-content-container").removeClass("in")}),jQuery(document).ready(function(){$(".dropdown").click(function(){$(".dropdown-menu",this).fadeToggle("fast")})});